import React from 'react';
import axios from 'axios';
import Card, {
    CardPrimaryContent,
    CardMedia,
    CardActions,
    CardActionButtons,
    CardActionIcons
} from "@material/react-card";
import { red100 } from 'material-ui/styles/colors';

class view extends React.Component{

    state={
        data:[],
        image: [],
    };
    
    componentDidMount () {
        let id = (this.props.match.url).substr(6)
        console.log('id',id);
        axios.get(`http://api.tvmaze.com/shows/${id}`).then(res => {
            this.setState({data2: this.props})
            console.log('rrr', this.props);
            console.log('reee', res);
            this.setState({data: res.data})
            this.setState({image: res.data.image})
            console.log('fff', res.data);
            console.log('qqq',this.state.data); 
            });
        }
        
    
    render(){
        return(
            <div className="App" style={{backgroundColor:red100}}>
    <Card>
      <CardPrimaryContent>
        <h1>Header</h1>
        <CardMedia imageUrl='./my/fancy/image.png' />
      </CardPrimaryContent>
 
      <CardActions>
        <CardActionButtons>
          <button>Click Me</button>
        </CardActionButtons>
 
        <CardActionIcons>
          <i>Click Me Too!</i>
        </CardActionIcons>
      </CardActions>
    </Card>
            </div>        
        )
        }
    }

export default view;
